package javatask;

public class Task8 {

	//character at given index within the string 
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		    String sc = "Welcome Java";
	        System.out.println("Original String = " + sc);

	        int index0 = sc.charAt(0);
	        int index2 = sc.charAt(10);

	        System.out.println("The character at position 0 is " + (char)index0);
	        System.out.println("The character at position 10 is " + (char)index2);
	}
}
